function ContributionsTable({ contributions, summary }) {
    return (
        <div>
            {contributions && contributions.contributors && (
                <div>
                    <p>Campaign finance information for the {contributions.cycle} election cycle:</p>
                    <table>
                        <thead>
                            <tr>
                                <th>Cash on Hand</th>
                                <th>Debt</th>
                            </tr>
                        </thead>
                    </table>

                    <table>
                        <thead>
                            <tr>
                                <th>Contributor</th>
                                <th>Amount</th>
                            </tr>
                        </thead>
                        <tbody>
                            {contributions.contributors.map((contributor, index) => (
                                <tr key={`contributor-${index}`}>
                                    <td>{contributor['@attributes'].org_name}</td>
                                    <td>
                                        {new Intl.NumberFormat('en-US', {
                                            style: 'currency',
                                            currency: 'USD',
                                            minimumFractionDigits: 0,
                                        }).format(contributor['@attributes'].total)}
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            )}
        </div>
    );
}

export default ContributionsTable;
