import { useState } from 'react';
import { getCivicInfoOfficialsByAddress, getOpenSecretsCandidateIds, getOpenSecretCandidatesInfo } from './api'

function GetRepByAddressForm() {

    const [representative, setRepresentative] = useState({});
    const [repOpenSecretsId, setRepOpenSecretsId] = useState({});
    const [formData, setFormData] = useState({
        zipCode: '',
    });

    const handleSubmit = async (event) => {
        event.preventDefault();

        const data = await getCivicInfoOfficialsByAddress(formData.zipCode);
        setRepresentative(data.representative);

        const ids = await getOpenSecretsCandidateIds(
            data.state,
            data.representative.name,
        );
        setRepId(ids);
        console.log('Officials IDs:', repId);

    // Check if the properties are available before accessing nested properties
    if (repId.representative && repId.representative.cid) {
        const representativeContribData = await getOpenSecretCandidatesInfo(repId.representative.cid);
        console.log(representativeContribData);
    }

};

    const handleZipCodeChange = (event) => {
        const value = event.target.value;
        const inputName = event.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }

    return (
        <>
            <p>Enter your zip code:</p>
            <form onSubmit={handleSubmit}>
                <input onChange={handleZipCodeChange} required placeholder='Zip code' type='number' id='zipCode' name='zipCode' className='form-control' />
                <button className="btn btn-primary">Submit</button>
            </form>
            <ul>
                <li>Representative: {representative.name}</li>
            </ul>
        </>
    );
};

export default GetRepByAddressForm;
