import { useState } from 'react';
import { getCivicInfoOfficialsByAddress, getOpenSecretsCandidateIds, getOpenSecretCandidatesInfo } from './api'

function GetRepByAddressForm() {

    const [representative, setRepresentative] = useState({});
    const [officialsIds, setOfficialsIds] = useState({});
    const [formData, setFormData] = useState({
        zipCode: '',
    });

    const handleSubmit = async (event) => {
        event.preventDefault();

        const data = await getCivicInfoOfficialsByAddress(formData.zipCode);
        setRepresentative(data.representative);
        setSenator1(data.senator1);
        setSenator2(data.senator2);

        const ids = await getOpenSecretsCandidateIds(
            data.state,
            data.representative.name,
            data.senator1.name,
            data.senator2.name
        );
        setOfficialsIds(ids);
        console.log('Officials IDs:', officialsIds);

    // Check if the properties are available before accessing nested properties
    if (officialsIds.representative && officialsIds.representative.cid) {
        const representativeContribData = await getOpenSecretCandidatesInfo(officialsIds.representative.cid);
        console.log(representativeContribData);
    }

    if (officialsIds.senator1 && officialsIds.senator1.cid) {
        const senator1ContribData = await getOpenSecretCandidatesInfo(officialsIds.senator1.cid);
        console.log(senator1ContribData);
    }

    if (officialsIds.senator2 && officialsIds.senator2.cid) {
        const senator2ContribData = await getOpenSecretCandidatesInfo(officialsIds.senator2.cid);
        console.log(senator2ContribData);
    }
};

    const handleZipCodeChange = (event) => {
        const value = event.target.value;
        const inputName = event.target.name;
        setFormData({
            ...formData,
            [inputName]: value
        });
    }


    return (
        <>
            <p>Enter your zip code:</p>
            <form onSubmit={handleSubmit}>
                <input onChange={handleZipCodeChange} required placeholder='Zip code' type='number' id='zipCode' name='zipCode' className='form-control' />
                <button className="btn btn-primary">Submit</button>
            </form>
            <ul>
                <li>Representative: {representative.name}</li>
                <li>Senators: {senator1.name} and {senator2.name}</li>
            </ul>
        </>
    );
};

export default GetRepByAddressForm;
