import { CIVIC_INFO_API_KEY, OPEN_SECRETS_API_KEY } from './apiKeys';

const getCivicInfoRepByAddress = async (zipCode) => {

    const apiUrl = 'https://www.googleapis.com/civicinfo/v2/representatives';
    const params = {
        address: zipCode,
        includeOffices: true,
        levels: 'country',
        roles: 'legislatorLowerBody',
        key: CIVIC_INFO_API_KEY,
    };

    const queryString = new URLSearchParams(params).toString();
    const urlWithParams = `${apiUrl}?${queryString}`;

    try {
        const response = await fetch(urlWithParams);
        if (!response.ok) {
            throw new Error(`HTTP error. Status: ${response.status}`);
        }
        const data = await response.json();

        function getOfficialIndicesByRole(offices, roleName) {
            let OfficialIndices = null;
            for (let office of offices) {
                if (office.name === roleName) {
                    OfficialIndices = office.officialIndices;
                    break;
                }
            }
            return OfficialIndices;
        }

        const indexRepresentative = getOfficialIndicesByRole(data.offices, 'U.S. Representative');

        return {
            representative: data.officials[indexRepresentative],
            state: data.normalizedInput.state,
        };

    } catch (error) {
        console.error('Error fetching data:', error);
        return {
            representative: {},
            state: '',
        }
    }
};

const getOpenSecretsCandidateIds = async (officialsState, representativeName) => {

    const apiUrl = 'http://www.opensecrets.org/api/'
    const params = {
        method: 'getLegislators',
        id: officialsState,
        apiKey: OPEN_SECRETS_API_KEY,
        output: 'json',
    }

    const urlWithParams = `${apiUrl}?method=${params.method}&id=${params.id}&apikey=${params.apiKey}&output=${params.output}`;

    try {
        const response = await fetch(urlWithParams);
        if (!response.ok) {
            throw new Error(`HTTP error. Status: ${response.status}`);
        }
        const data = await response.json();

        function getLegislatorIdFromName(data, name) {
            for (let legislator of data) {
                if (legislator['@attributes'].firstlast === name) {
                    return {
                        firstlast: legislator['@attributes'].firstlast,
                        cid: legislator['@attributes'].cid,
                    };
                }
            }
            return null;
        }

        const representativeId = getLegislatorIdFromName(data.response.legislator, representativeName);

        return {
            representative: representativeId,
        };

    } catch (error) {
        console.error('Error fetching data:', error);
        return {
            representativeId: '',
        }
    }

};

const getOpenSecretCandidatesInfo = async (officialId) => {

    const apiUrl = 'http://www.opensecrets.org/api/'
    const params = {
        method: 'candContrib',
        apiKey: OPEN_SECRETS_API_KEY,
        cid: officialId,
        cycle: '2000', // out of range cycle returns most recent cycle
        output: 'json',
    }

    const urlWithParams = `${apiUrl}?method=${params.method}&apikey=${params.apiKey}&cid=${params.cid}&cycle=${params.cycle}&output=${params.output}`;

    try {
        const response = await fetch(urlWithParams);
        if (!response.ok) {
            throw new Error(`HTTP error. Status: ${response.status}`);
        }
        const data = await response.json();

        console.log(data)

    } catch (error) {
        console.error('Error fetching data:', error);
        return {
            representativeContribInfo: '',
        }
    }
}

export { getCivicInfoRepByAddress, getOpenSecretsCandidateIds, getOpenSecretCandidatesInfo };
