                <div>
                    <CongressGovPhoto info={proPublicaInfo} photo={repPhoto} />
                    <h2>{repInfo.representative.name}</h2>
                    <p>{repInfo.office.name}</p>
                    <p>{repInfo.representative.party}</p>
