function RepresentativeInfo({ repInfo }) {
    return (
        <div>
            {repInfo.representative && repInfo.office && (
                <div>
                    <h2>{repInfo.representative.name}</h2>
                    <p>Office: {repInfo.office.name}</p>
                    <p>Party: {repInfo.representative.party}</p>
                    <p>District: {repInfo.office.district}</p>
                    {repInfo.representative.channels && (
                        <div>
                            <h3>Social Media Channels:</h3>
                            {repInfo.representative.channels.map((channel, index) => (
                                <div key={`channel-${index}`}>
                                    <p>{channel.type}{channel.id}</p>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default RepresentativeInfo;
